import{PageFlip}from"page-flip";var tempo=function(){document.addEventListener("DOMContentLoaded",function(){var e=.43*window.innerWidth,t=.42*window.innerHeight;const n=new PageFlip(document.getElementById("demoBookExample"),{width:e/2,height:t,size:"stretch",minWidth:0,maxWidth:e,minHeight:420,maxHeight:1350,maxShadowOpacity:.2,showCover:!0,mobileScrollSupport:!1});var o=document.getElementsByClassName("botonTelefono");for(let e=0;e<o.length;e++)o[e].addEventListener("click",function(){var t=parseInt(o[e].id.replace("pag",""));n.flip(t,"top")});n.loadFromHTML(document.querySelectorAll(".page")),document.querySelector(".page-total").innerText=n.getPageCount(),document.querySelector(".page-orientation").innerText=n.getOrientation(),document.querySelector(".btn-prev").addEventListener("click",()=>{n.flipPrev()}),document.querySelector(".btn-next").addEventListener("click",()=>{n.flipNext()}),n.on("flip",e=>{document.querySelector(".page-current").innerText=e.data+1}),n.on("changeState",e=>{document.querySelector(".page-state").innerText=e.data}),n.on("changeOrientation",e=>{document.querySelector(".page-orientation").innerText=e.data})})},funcion=new tempo;export{funcion};